mapper.select:
SELECT p.*, COUNT( pl.id ) AS like_count FROM post_record p LEFT JOIN user_post_like pl ON p.id = pl.post_id
p.is_featured = 1
AND p.first_level_category = #{firstLevelCategory}
AND p.second_level_category = #{secondLevelCategory}
GROUP BY p.id ORDER BY p.official_weight ASC, like_count DESC
